document.addEventListener("DOMContentLoaded",()=>{const e=document.querySelector(".sweet-1"),t=document.querySelector(".sweet-2"),r=document.querySelector(".sweet-3"),o=document.querySelector(".sweet-4"),n=document.querySelector("#form"),i=Swal.mixin({toast:!0,position:"top-end",showConfirmButton:!1,timer:3e3,timerProgressBar:!0,didOpen:e=>{e.addEventListener("mouseenter",Swal.stopTimer),e.addEventListener("mouseleave",Swal.resumeTimer)}});n.addEventListener("submit",n=>{""!=e.value.trim()&&""!=t.value.trim()&&""!=r.value.trim()&&""!=o.value.trim()||(n.preventDefault(),i.fire({icon:"error",title:"Please enter all the details."}))})});